y2 = y(:);

coltab = [0 0 0;
          130 0 0;
          200 30 0;
          200 70 0;
          255 110 0;
          255 170 0;]/256;
      
RGB=[];
counter=1;
row=1;
for i=1:6
   color=coltab(i,:);
   for j=1:180
       RGB(row,:)=color;
       row=row+1;
       if mod(j, 30) == 0
         counter = counter + 1;
         break
       end
   end
end

